kestirilen_par <- function(veri, par=3){

  if(par==3){
    model <- mirt::mirt(veri, # cevap matrisi
                         1,  # tek boyutlu model
                         itemtype = "3PL", # 3PL
                         verbose = FALSE, 
                         technical = list(NCYCLES = 1000,
                                          message = FALSE))
  }else {
        model <- mirt::mirt(veri, # cevap matrisi
                         1,  # tek boyutlu model
                         itemtype = "2PL", # 2pl
                         verbose = FALSE, 
                         technical = list(NCYCLES = 1000,
                                          message = FALSE))
  }
# Madde parametreleri
kestirim <- as.data.frame(mirt::coef(model, IRTpars = TRUE, 
simplify = TRUE)$item[,1:3])
kestirim
}

